import React, { useState, useEffect } from 'react';
import './UserHome.css';
import { Link, useNavigate } from 'react-router-dom';
import { FaBars } from 'react-icons/fa';

const UserHome = () => {
  const navigate = useNavigate();
  const [resources, setResources] = useState([]);
  const [isSidebarVisible, setSidebarVisible] = useState(false);

  useEffect(() => {
    // Fetch resources on component mount
    fetch('http://localhost:8080/api/resources') // Update with your backend URL
      .then((response) => response.json())
      .then((data) => setResources(data))
      .catch((error) => console.error('Error fetching resources:', error));
  }, []);

  const handleCoverClick = (resourceId) => {
    navigate(`/resource/${resourceId}`);
  };

  const toggleSidebar = () => {
    setSidebarVisible(!isSidebarVisible);
  };

  const closeSidebar = () => {
    setSidebarVisible(false);
  };

  return (
    <div className="home-container">
      {!isSidebarVisible && (
        <button onClick={toggleSidebar} className="menu-toggle">
          <FaBars />
        </button>
      )}

      {isSidebarVisible && (
        <aside className="sidebar">
          <h2 className="sidebar-title" onClick={closeSidebar}>EduConnect</h2>
          <nav className="sidebar-menu">
            <Link to="/user-profile" className="menu-item" onClick={closeSidebar}>User Profile</Link>
            <Link to="/borrow-books" className="menu-item" onClick={closeSidebar}>Borrow Books</Link>
            <Link to="/buy-books" className="menu-item" onClick={closeSidebar}>Buy Books</Link>
            <Link to="/history" className="menu-item" onClick={closeSidebar}>History</Link>
            <Link to="/return-books" className="menu-item" onClick={closeSidebar}>Return Books</Link>
          </nav>
          <button onClick={() => navigate('/login')} className="logout-button">Logout</button>
        </aside>
      )}

      <div className={`content-wrapper ${isSidebarVisible ? 'shifted-content' : ''}`}>
        <header className="header">
          <h1 className="header-title">Welcome to EduConnect</h1>
        </header>

        <div className="main-content">
          <div className="resource-gallery">
            {resources.map((resource) => (
              <div key={resource.id} className="resource-card">
                <img
                  src={`http://localhost:8080/${resource.coverPhotoUrl}`} // Adjust for your backend's static file path
                  alt={resource.title}
                  className="cover-photo"
                  onClick={() => handleCoverClick(resource.id)}
                />
                <p>{resource.title}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserHome;
