import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./UserHome.css";

const UserHome = () => {
  const [resources, setResources] = useState([]);
  const navigate = useNavigate();

  // Fetch resources when component mounts
  useEffect(() => {
    fetch("http://localhost:8080/api/resources")
      .then((response) => response.json())
      .then((data) => setResources(data))
      .catch((error) => console.error("Error fetching resources:", error));
  }, []);

  const handleResourceClick = (id) => {
    navigate(`/resource/${id}`); // Navigate to the detail page with resource ID
  };

  return (
    <div className="home-container">
      <header className="header">
        <h1>Welcome to EduConnect</h1>
      </header>
      <div className="resource-grid">
        {resources.map((resource) => (
          <div
            key={resource.id}
            className="resource-card"
            onClick={() => handleResourceClick(resource.id)}
          >
            {resource.coverPhotoUrl && (
              <img
                src={`http://localhost:8080${resource.coverPhotoUrl}`}
                alt={resource.title}
                className="resource-cover"
              />
            )}
            <h3>{resource.title}</h3>
            <p>{resource.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserHome;
