import React, { useState } from 'react';
import './AdminHome.css';
import ManageUsers from './ManageUsers';
import { FaHome, FaUpload, FaFolder, FaUsers, FaSignOutAlt } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import UploadForm from './UploadResource'; // Import Upload Form

function AdminHome() {
    const [activeTab, setActiveTab] = useState("Dashboard");
    const navigate = useNavigate(); // Initialize navigate function

    // Sample company data with images
    const companies = [
        { id: 1, name: "Google", img: "https://logo.clearbit.com/google.com" },
        { id: 2, name: "Microsoft", img: "https://logo.clearbit.com/microsoft.com" },
        { id: 3, name: "Amazon", img: "https://logo.clearbit.com/amazon.com" },
        { id: 4, name: "Facebook", img: "https://logo.clearbit.com/facebook.com" },
        { id: 5, name: "Apple", img: "https://logo.clearbit.com/apple.com" }
    ];

    const renderContent = () => {
        switch (activeTab) {
            case "Upload":
                return <UploadForm />;
            case "Organize":
                return (
                    <div className="organize-container">
                        <h2>Organize Job Applications</h2>
                        <div className="company-grid">
                            {companies.map((company) => (
                                <div key={company.id} className="company-card">
                                    <img src={company.img} alt={company.name} className="company-logo" />
                                    <h3>{company.name}</h3>
                                </div>
                            ))}
                        </div>
                    </div>
                );
            case "Manage":
                return <ManageUsers />;
            default:
                return <h2>Welcome to the Admin Dashboard</h2>;
        }
    };

    const handleLogout = () => {
        // Optional: Add logic to clear session tokens, etc.
        console.log("Logged out");
        navigate('/login'); // Redirects to the Login page
    };

    return (
        <div className="admin-home">
            <div className="sidebar">
                <h1 className="logo">Placement Portal</h1>
                <ul>
                    <li onClick={() => setActiveTab("Dashboard")} className={activeTab === "Dashboard" ? "active" : ""}>
                        <FaHome className="menu-icon" /> Dashboard
                    </li>
                    <li onClick={() => setActiveTab("Upload")} className={activeTab === "Upload" ? "active" : ""}>
                        <FaUpload className="menu-icon" /> Upload Job Applications
                    </li>
                    <li onClick={() => setActiveTab("Organize")} className={activeTab === "Organize" ? "active" : ""}>
                        <FaFolder className="menu-icon" /> Organize Job Applications
                    </li>
                    <li onClick={() => setActiveTab("Manage")} className={activeTab === "Manage" ? "active" : ""}>
                        <FaUsers className="menu-icon" /> Manage User Access
                    </li>
                </ul>
                <button className="logout-button" onClick={handleLogout}>
                    <FaSignOutAlt className="menu-icon" /> Logout
                </button>
            </div>
            <div className="main-content">
                {renderContent()}
            </div>
        </div>
    );
}

export default AdminHome;
