import React, { useState } from 'react';
import './AdminHome.css';

function AdminHome() {
    const [activeTab, setActiveTab] = useState("Dashboard");

    const renderContent = () => {
        switch (activeTab) {
            case "Upload":
                return <h2>Upload and organize educational resources</h2>;
            case "Organize":
                return <h2>Organize resources into categories</h2>;
            case "Manage":
                return <h2>Manage user access</h2>;
            default:
                return <h2>Welcome to the Admin Dashboard</h2>;
        }
    };

    return (
        <div className="admin-home">
            <div className="sidebar">
                <h1 className="logo">Admin</h1>
                <ul>
                    <li onClick={() => setActiveTab("Dashboard")} className={activeTab === "Dashboard" ? "active" : ""}>Dashboard</li>
                    <li onClick={() => setActiveTab("Upload")} className={activeTab === "Upload" ? "active" : ""}>Upload Resources</li>
                    <li onClick={() => setActiveTab("Organize")} className={activeTab === "Organize" ? "active" : ""}>Organize Resources</li>
                    <li onClick={() => setActiveTab("Manage")} className={activeTab === "Manage" ? "active" : ""}>Manage User Access</li>
                </ul>
            </div>
            <div className="main-content">
                {renderContent()}
            </div>
        </div>
    );
}

export default AdminHome;
