import React, { useState } from 'react';
import './UploadResource.css';

function UploadResource() {
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        category: '',
        tags: '',
        author: '',
        publicationDate: '',
    });
    const [resourceFile, setResourceFile] = useState(null);
    const [coverPhoto, setCoverPhoto] = useState(null);
    const [message, setMessage] = useState("");

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleFileChange = (e) => {
        const { name, files } = e.target;
        if (name === 'resourceFile') {
            setResourceFile(files[0]);
        } else if (name === 'coverPhoto') {
            setCoverPhoto(files[0]);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const data = new FormData();
        Object.keys(formData).forEach((key) => {
            data.append(key, formData[key]);
        });
        if (resourceFile) data.append('resourceFile', resourceFile);
        if (coverPhoto) data.append('coverPhoto', coverPhoto);

        try {
            const response = await fetch('http://localhost:8080/api/upload', {
                method: 'POST',
                body: data,
            });

            const result = await response.json();
            if (response.ok) {
                setMessage(result.message);
            } else {
                setMessage('Error: Could not upload the resource');
            }
        } catch (error) {
            console.error('Upload error:', error);
            setMessage('Error: An error occurred during upload');
        }
    };

    return (
        <div className="upload-container">
            <h2>Upload New Resource</h2>
            <form onSubmit={handleSubmit} className="upload-form">
                <input type="text" name="title" placeholder="Title" onChange={handleChange} required />
                <textarea name="description" placeholder="Description" onChange={handleChange} />
                <input type="text" name="category" placeholder="Category" onChange={handleChange} />
                <input type="text" name="tags" placeholder="Tags (comma-separated)" onChange={handleChange} />
                <input type="text" name="author" placeholder="Author" onChange={handleChange} />
                <input type="date" name="publicationDate" onChange={handleChange} />
                <input type="file" name="resourceFile" accept=".pdf,.doc,.docx,.ppt,.pptx" onChange={handleFileChange} required />
                <input type="file" name="coverPhoto" accept="image/*" onChange={handleFileChange} />

                <button type="submit" className="upload-button">Upload Resource</button>
            </form>
            {message && <p className="message">{message}</p>}
        </div>
    );
}

export default UploadResource;
