import React from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

// Register required chart.js components
ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
);

function Performance() {
    // Sample data for subjects and marks
    const subjects = [
        { name: 'Mathematics', marks: 85 },
        { name: 'Physics', marks: 90 },
        { name: 'Computer Science', marks: 92 },
        { name: 'Electronics', marks: 88 },
        { name: 'Mechanical Engineering', marks: 80 },
        { name: 'Civil Engineering', marks: 75 },
    ];

    // Chart data
    const chartData = {
        labels: subjects.map(subject => subject.name), // X-axis labels (subject names)
        datasets: [
            {
                label: 'Marks',
                data: subjects.map(subject => subject.marks), // Y-axis data (marks)
                borderColor: 'rgba(75, 192, 192, 1)', // Line color
                backgroundColor: 'rgba(75, 192, 192, 0.2)', // Background color for the line
                fill: true,
            },
        ],
    };

    // Chart options
    const chartOptions = {
        responsive: true,
        plugins: {
            title: {
                display: true,
                text: 'Student Performance in Engineering Subjects',
            },
            legend: {
                position: 'top',
            },
        },
    };

    return (
        <div style={{ padding: '20px', backgroundColor: '#121212', color: '#fff', minHeight: '100vh' }}>
            <h1 style={{ textAlign: 'center', marginBottom: '20px' }}>Engineering Subjects Performance</h1>
            <div style={{ display: 'flex', justifyContent: 'center', flexDirection: 'column', alignItems: 'center' }}>
                <table style={{ width: '80%', borderCollapse: 'collapse', marginBottom: '20px' }}>
                    <thead>
                        <tr>
                            <th style={{ border: '1px solid #ccc', padding: '10px', textAlign: 'center' }}>Subject</th>
                            <th style={{ border: '1px solid #ccc', padding: '10px', textAlign: 'center' }}>Marks</th>
                        </tr>
                    </thead>
                    <tbody>
                        {subjects.map((subject, index) => (
                            <tr key={index} style={{ borderBottom: '1px solid #ddd' }}>
                                <td style={{ padding: '8px', textAlign: 'center' }}>{subject.name}</td>
                                <td style={{ padding: '8px', textAlign: 'center' }}>{subject.marks}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>

                <div style={{ width: '80%', maxWidth: '800px' }}>
                    <Line data={chartData} options={chartOptions} />
                </div>
            </div>
        </div>
    );
}

export default Performance;
