import React, { useState, useEffect } from 'react';
import './LoginSignup.css';
import { Link, useNavigate } from 'react-router-dom';

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [captcha, setCaptcha] = useState('');
    const [captchaInput, setCaptchaInput] = useState('');
    const [isCaptchaValid, setIsCaptchaValid] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        generateCaptcha();
    }, []);

    const generateCaptcha = () => {
        const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
        let result = '';
        for (let i = 0; i < 6; i++) {
            result += characters.charAt(Math.floor(Math.random() * characters.length));
        }
        setCaptcha(result);
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        if (captcha !== captchaInput) {
            setIsCaptchaValid(false);
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/login", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ username, password })
            });

            const data = await response.json();
            if (response.ok) {
                alert(data.message);
                if (data.role === "ADMIN") navigate("/admin-home");
                else if (data.role === "USER") navigate("/user-home");
                else if (data.role === "FACULTY") navigate("/faculty-home");
            } else {
                alert("Invalid credentials. Please try again.");
            }
        } catch (error) {
            console.error("Login error:", error);
            alert("An error occurred during login.");
        }
    };

    return (
        <div className="auth-container login-container">
            <form onSubmit={handleLogin} className="auth-form">
                <h2>EduConnect</h2>
                <h2>Login</h2>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <div className="captcha-container">
                    <div className="captcha-display" onClick={generateCaptcha}>{captcha}</div>
                </div>
                <input
                    type="text"
                    placeholder="Enter CAPTCHA"
                    value={captchaInput}
                    onChange={(e) => {
                        setCaptchaInput(e.target.value);
                        setIsCaptchaValid(true);
                    }}
                    required
                    className={isCaptchaValid ? '' : 'error'}
                />
                {!isCaptchaValid && <div className="error-message">Incorrect CAPTCHA</div>}
                <button type="submit">Log In</button>
                <div className="remember-forgot">
                    <Link to="/forgot-password">Forgot Password?</Link>
                </div>
                <p>New user? <Link to="/signup">Register</Link></p>
            </form>
        </div>
    );
}

export default Login;
