import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./UserHome.css";

const UserHome = () => {
  const navigate = useNavigate();
  const [resources, setResources] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/api/resources")
      .then((response) => response.json())
      .then((data) => setResources(data));
  }, []);

  const navigateToUpload = () => {
    navigate("/upload-resource");
  };

  return (
    <div className="user-home">
      <h2>User Dashboard</h2>
      <div className="resource-list">
        {resources.map((resource) => (
          <div key={resource.id} className="resource-card">
            <img
              src={`data:image/jpeg;base64,${Buffer.from(resource.coverPhoto).toString("base64")}`}
              alt={resource.title}
              className="cover-photo"
            />
            <h3>{resource.title}</h3>
            <p>{resource.description}</p>
            <button className="view-details">View Details</button>
          </div>
        ))}
      </div>
      <button onClick={navigateToUpload} className="upload-btn">Upload New Resource</button>
    </div>
  );
};

export default UserHome;
