import React, { useState, useEffect } from 'react';
import './UserHome.css';
import { useNavigate } from 'react-router-dom';
import { FaBars } from 'react-icons/fa';

const UserHome = () => {
  const navigate = useNavigate();
  const [resources, setResources] = useState([]);
  const [isSidebarVisible, setSidebarVisible] = useState(false);

  useEffect(() => {
    // Fetch resources from the backend
    fetch('http://localhost:8080/api/resources') // Replace with your backend API URL
      .then((response) => response.json())
      .then((data) => setResources(data))
      .catch((error) => console.error('Error fetching resources:', error));
  }, []);

  const handleCoverClick = (resourceId) => {
    navigate(`/resource/${resourceId}`);
  };

  const toggleSidebar = () => {
    setSidebarVisible(!isSidebarVisible);
  };

  const closeSidebar = () => {
    setSidebarVisible(false);
  };

  return (
    <div className="home-container">
      {!isSidebarVisible && (
        <button onClick={toggleSidebar} className="menu-toggle">
          <FaBars />
        </button>
      )}

      {isSidebarVisible && (
        <aside className="sidebar">
          <h2 className="sidebar-title" onClick={closeSidebar}>EduConnect</h2>
        </aside>
      )}

      <div className={`content-wrapper ${isSidebarVisible ? 'shifted-content' : ''}`}>
        <header className="header">
          <h1 className="header-title">Educational Resources</h1>
        </header>

        <div className="main-content">
          <div className="resource-gallery">
            {resources.map((resource) => (
              <div key={resource.id} className="resource-card">
                <img
                  src={`http://localhost:8080/${resource.coverPhotoUrl}`} // Replace with the correct backend URL
                  alt={resource.title}
                  className="cover-photo"
                  onClick={() => handleCoverClick(resource.id)}
                />
                <p>{resource.title}</p>
                {/* Display PDF link */}
                {resource.fileUrl && (
                  <a
                    href={`http://localhost:8080/${resource.fileUrl}`} // Replace with the correct backend URL
                    target="_blank"
                    rel="noopener noreferrer"
                    className="pdf-link"
                  >
                    View PDF
                  </a>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserHome;
