import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const ResourceDetails = () => {
  const { id } = useParams(); // Get the resource ID from the URL
  const [resource, setResource] = useState(null); // State to store resource details
  const [error, setError] = useState(null); // State to handle errors
  const [loading, setLoading] = useState(true); // State to handle loading

  useEffect(() => {
    // Fetch the resource details when the component loads
    const fetchResource = async () => {
      try {
        const response = await axios.get(`/api/resources/${id}`);
        setResource(response.data); // Set the resource data
      } catch (err) {
        setError('Resource not found or failed to load');
      } finally {
        setLoading(false); // End loading state
      }
    };

    fetchResource();
  }, [id]);

  if (loading) return <p>Loading...</p>; // Display loading message
  if (error) return <p style={{ color: 'red' }}>{error}</p>; // Display error message

  return (
    <div style={{ padding: '20px' }}>
      <h1>{resource.title}</h1>
      <p><strong>Description:</strong> {resource.description}</p>
      <p><strong>Category:</strong> {resource.category}</p>
      <p><strong>Author:</strong> {resource.author}</p>
      <p><strong>Publication Date:</strong> {new Date(resource.publicationDate).toDateString()}</p>

      {resource.coverPhoto && (
        <img
          src={`data:image/jpeg;base64,${btoa(String.fromCharCode(...new Uint8Array(resource.coverPhoto)))}`}
          alt="Cover"
          style={{ width: '300px', height: 'auto', marginTop: '20px' }}
        />
      )}

      <div style={{ marginTop: '20px' }}>
        <a href={`/api/resources/${id}/pdf`} target="_blank" rel="noopener noreferrer">
          View PDF
        </a>
      </div>
    </div>
  );
};

export default ResourceDetails;
