import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  FaUser,
  FaBook,
  FaBriefcase,
  FaHistory,
  FaUndoAlt,
  FaSignOutAlt,
  FaBars,
} from "react-icons/fa";
import bookGif from "./gif/book.gif"; // Example GIF for hover effect on Textbooks
import notesGif from "./gif/notes.gif"; // GIF for Lecture Notes
import researchGif from "./gif/research.gif"; // GIF for Research Papers
import "./UserHome.css";

const UserHome = () => {
  const navigate = useNavigate();
  const [isSidebarVisible, setSidebarVisible] = useState(false);
  const [isHovered, setIsHovered] = useState({});
  const [resources, setResources] = useState([]);

  // Fetch resources from the backend when the component mounts
  useEffect(() => {
    fetch("http://localhost:8080/api/resource")
      .then((response) => response.json())
      .then((data) => setResources(data))
      .catch((error) => console.error("Error fetching resources:", error));
  }, []);

  // Handle logout functionality
  const handleLogout = () => {
    navigate("/login"); // Redirect to the login page
  };

  // Toggle sidebar visibility
  const toggleSidebar = () => {
    setSidebarVisible(!isSidebarVisible);
  };

  // Close sidebar
  const closeSidebar = () => {
    setSidebarVisible(false);
  };

  // Handle clicks for different modules
  const handleModuleClick = (path) => {
    navigate(path); // Redirect to respective page
  };

  // Manage mouse enter/leave events for hover effects on modules
  const handleMouseEnter = (module) => {
    setIsHovered((prev) => ({ ...prev, [module]: true }));
  };

  const handleMouseLeave = (module) => {
    setIsHovered((prev) => ({ ...prev, [module]: false }));
  };

  return (
    <div className="home-container">
      {/* Sidebar toggle button */}
      {!isSidebarVisible && (
        <button onClick={toggleSidebar} className="menu-toggle">
          <FaBars />
        </button>
      )}

      {/* Sidebar */}
      {isSidebarVisible && (
        <aside className="sidebar">
          <h2 className="sidebar-title" onClick={closeSidebar}>
            PlacementConnect
          </h2>
          <nav className="sidebar-menu">
            <a
              className="menu-item"
              onClick={() => {
                navigate("/user-profile");
                closeSidebar();
              }}
            >
              <FaUser className="menu-icon" /> User Profile
            </a>
            <a
              className="menu-item"
              onClick={() => {
                navigate("/placement-opportunities");
                closeSidebar();
              }}
            >
              <FaBriefcase className="menu-icon" /> Placement Opportunities
            </a>
            <a
              className="menu-item"
              onClick={() => {
                navigate("/placement-history");
                closeSidebar();
              }}
            >
              <FaHistory className="menu-icon" /> Placement History
            </a>
            <a
              className="menu-item"
              onClick={() => {
                navigate("/preparation-material");
                closeSidebar();
              }}
            >
              <FaBook className="menu-icon" /> Preparation Material
            </a>
          </nav>
          <button onClick={handleLogout} className="logout-button">
            <FaSignOutAlt className="menu-icon" /> Logout
          </button>
        </aside>
      )}

      {/* Main content */}
      <div
        className={`content-wrapper ${isSidebarVisible ? "shifted-content" : ""
          }`}
      >
        <header className="header">
          <h1 className="header-title">Welcome to Placement Portal</h1>
        </header>

        <div className="main-content">
          {/* Module Cards */}
          <div className="modules">
            <div
              className="module-card"
              onClick={() => handleModuleClick("/placement-opportunities")}
            >
              <div
                className="module-background"
                onMouseEnter={() => handleMouseEnter("opportunities")}
                onMouseLeave={() => handleMouseLeave("opportunities")}
              >
                <FaBriefcase className="module-icon" />
                <p className="module-text">Opportunities</p>
                {isHovered["opportunities"] && (
                  <img
                    src={researchGif}
                    alt="Placement Opportunities GIF"
                    className="gif-hover"
                  />
                )}
              </div>
            </div>
            <div
              className="module-card"
              onClick={() => handleModuleClick("/preparation-material")}
            >
              <div
                className="module-background"
                onMouseEnter={() => handleMouseEnter("preparation")}
                onMouseLeave={() => handleMouseLeave("preparation")}
              >
                <FaBook className="module-icon" />
                <p className="module-text">Preparation Material</p>
                {isHovered["preparation"] && (
                  <img
                    src={bookGif}
                    alt="Preparation Material GIF"
                    className="gif-hover"
                  />
                )}
              </div>
            </div>
            <div
              className="module-card"
              onClick={() => handleModuleClick("/placement-history")}
            >
              <div
                className="module-background"
                onMouseEnter={() => handleMouseEnter("history")}
                onMouseLeave={() => handleMouseLeave("history")}
              >
                <FaHistory className="module-icon" />
                <p className="module-text">Placement History</p>
                {isHovered["history"] && (
                  <img
                    src={notesGif}
                    alt="Placement History GIF"
                    className="gif-hover"
                  />
                )}
              </div>
            </div>
          </div>

          {/* Resource Grid */}
          <h2>Available Resources</h2>
          <div className="resource-grid">
            {resources.map((resource) => (
              <div key={resource.id} className="resource-card">
                {resource.coverPhotoUrl && (
                  <img
                    src={`http://localhost:8080${resource.coverPhotoUrl}`}
                    alt="Cover"
                    className="resource-cover"
                  />
                )}
                <h3>{resource.title}</h3>
                <p>{resource.description}</p>
                <button
                  onClick={() => navigate(`/resource/${resource.id}`)}
                >
                  View Details
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserHome;
