import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { ChevronDown, Users, Lightbulb, Target, Clock } from 'lucide-react';

const AboutUs = () => {
  const [activeTab, setActiveTab] = useState(0);
  const [hoveredFeature, setHoveredFeature] = useState(null);

  const features = [
    {
      icon: <Users className="w-6 h-6" />,
      title: "Community Learning",
      description: "Join a vibrant community of learners and educators worldwide"
    },
    {
      icon: <Lightbulb className="w-6 h-6" />,
      title: "Smart Learning",
      description: "AI-powered personalized learning paths and recommendations"
    },
    {
      icon: <Target className="w-6 h-6" />,
      title: "Goal Tracking",
      description: "Set and track your educational goals with detailed analytics"
    },
    {
      icon: <Clock className="w-6 h-6" />,
      title: "24/7 Support",
      description: "Round-the-clock assistance for all your learning needs"
    }
  ];

  return (
    <div className="h-screen w-full overflow-hidden bg-gradient-to-br from-gray-900 to-blue-900">
      {/* Background Image with Overlay */}
      <div 
        className="absolute inset-0 bg-cover bg-center bg-no-repeat opacity-20"
        style={{ backgroundImage: `url('/background-image.jpg')` }}
      />
      
      {/* Navbar */}
      <nav className="relative z-20 bg-black/20 backdrop-blur-sm border-b border-white/10">
        <div className="max-w-7xl mx-auto px-4">
          <div className="flex items-center justify-between h-16">
            <Link to="/" className="flex-shrink-0 group">
              <span className="text-white text-2xl font-bold bg-gradient-to-r from-blue-400 to-purple-500 bg-clip-text text-transparent group-hover:scale-105 transition-transform duration-300">
                EduConnect
              </span>
            </Link>
            <div className="flex space-x-2">
              <Link to="/" className="text-white/90 hover:bg-white/10 px-4 py-2 rounded-lg transition-all duration-300 hover:scale-105">
                Home
              </Link>
              <Link to="/about" className="bg-white/10 text-white px-4 py-2 rounded-lg hover:scale-105 transition-transform">
                About
              </Link>
              <Link to="/contact" className="text-white/90 hover:bg-white/10 px-4 py-2 rounded-lg transition-all duration-300 hover:scale-105">
                Contact
              </Link>
            </div>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <div className="relative z-10 h-[calc(100vh-4rem)] overflow-y-auto scrollbar-hide">
        <div className="max-w-7xl mx-auto px-4 py-12">
          <div className="space-y-12">
            {/* Header Section with Animation */}
            <div className="text-center space-y-6">
              <h1 className="text-6xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-400 via-purple-500 to-pink-500 animate-gradient">
                About Us
              </h1>
              <p className="text-xl text-gray-300 max-w-3xl mx-auto leading-relaxed">
                EduConnect is a cutting-edge platform designed to transform the educational landscape through innovative technology and collaborative learning solutions.
              </p>
            </div>
            
            {/* Interactive Features Grid */}
            <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-6">
              {features.map((feature, index) => (
                <div
                  key={index}
                  className="group bg-white/5 backdrop-blur-sm rounded-xl border border-white/10 p-6 hover:bg-white/10 transition-all duration-300 transform hover:-translate-y-1"
                  onMouseEnter={() => setHoveredFeature(index)}
                  onMouseLeave={() => setHoveredFeature(null)}
                >
                  <div className={`text-blue-400 mb-4 transform transition-transform duration-300 ${hoveredFeature === index ? 'scale-110' : ''}`}>
                    {feature.icon}
                  </div>
                  <h3 className="text-xl font-semibold mb-3 text-blue-400">{feature.title}</h3>
                  <p className="text-gray-300">{feature.description}</p>
                </div>
              ))}
            </div>
            
            {/* Mission, Vision, Values Tabs */}
            <div className="bg-white/5 backdrop-blur-sm rounded-xl border border-white/10 p-8">
              <div className="flex space-x-4 mb-6">
                {['Mission', 'Vision', 'Values'].map((tab, index) => (
                  <button
                    key={index}
                    onClick={() => setActiveTab(index)}
                    className={`px-6 py-2 rounded-lg transition-all duration-300 ${
                      activeTab === index 
                        ? 'bg-white/10 text-white' 
                        : 'text-gray-400 hover:text-white hover:bg-white/5'
                    }`}
                  >
                    {tab}
                  </button>
                ))}
              </div>
              <div className="transition-all duration-300">
                {activeTab === 0 && (
                  <div className="space-y-4 animate-fadeIn">
                    <h3 className="text-2xl font-semibold text-blue-400">Our Mission</h3>
                    <p className="text-gray-300">To create seamless and engaging educational experiences that empower learners worldwide.</p>
                  </div>
                )}
                {activeTab === 1 && (
                  <div className="space-y-4 animate-fadeIn">
                    <h3 className="text-2xl font-semibold text-purple-400">Our Vision</h3>
                    <p className="text-gray-300">To revolutionize education through technology-driven collaboration and innovation.</p>
                  </div>
                )}
                {activeTab === 2 && (
                  <div className="space-y-4 animate-fadeIn">
                    <h3 className="text-2xl font-semibold text-pink-400">Our Values</h3>
                    <p className="text-gray-300">Innovation, collaboration, and excellence in everything we do.</p>
                  </div>
                )}
              </div>
            </div>

            {/* Why Choose Us Section with Enhanced Interactivity */}
            <div className="bg-white/5 backdrop-blur-sm rounded-xl border border-white/10 p-8">
              <h2 className="text-3xl font-semibold mb-8 text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-500">
                Why Choose EduConnect?
              </h2>
              <div className="grid md:grid-cols-2 gap-8">
                <div className="space-y-6">
                  {[
                    'Cutting-edge learning management system',
                    'Real-time collaboration tools'
                  ].map((item, index) => (
                    <div 
                      key={index} 
                      className="flex items-center space-x-3 group hover:bg-white/5 p-4 rounded-lg transition-all duration-300"
                    >
                      <div className="w-2 h-2 bg-blue-400 rounded-full group-hover:scale-125 transition-transform duration-300"></div>
                      <span className="text-gray-300 group-hover:text-white transition-colors duration-300">{item}</span>
                    </div>
                  ))}
                </div>
                <div className="space-y-6">
                  {[
                    'Personalized learning experiences',
                    '24/7 support for students and faculty'
                  ].map((item, index) => (
                    <div 
                      key={index} 
                      className="flex items-center space-x-3 group hover:bg-white/5 p-4 rounded-lg transition-all duration-300"
                    >
                      <div className="w-2 h-2 bg-purple-400 rounded-full group-hover:scale-125 transition-transform duration-300"></div>
                      <span className="text-gray-300 group-hover:text-white transition-colors duration-300">{item}</span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AboutUs;
