import React from 'react';

function Performance() {
  // Sample data for subjects and performance
  const subjects = [
    { name: 'Mathematics', marks: 85 },
    { name: 'Physics', marks: 90 },
    { name: 'Chemistry', marks: 78 },
    { name: 'Computer Science', marks: 92 },
    { name: 'Electrical Engineering', marks: 88 },
    { name: 'Mechanical Engineering', marks: 80 },
  ];

  const averageMarks = (
    subjects.reduce((acc, subject) => acc + subject.marks, 0) / subjects.length
  ).toFixed(2);

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Student Performance</h1>
      
      <div style={styles.subjectsContainer}>
        <h2 style={styles.heading}>Engineering Subjects</h2>
        
        <div style={styles.table}>
          <div style={styles.tableRow}>
            <div style={styles.tableHeader}>Subject</div>
            <div style={styles.tableHeader}>Marks</div>
          </div>
          {subjects.map((subject, index) => (
            <div key={index} style={styles.tableRow}>
              <div style={styles.tableData}>{subject.name}</div>
              <div style={styles.tableData}>{subject.marks}%</div>
            </div>
          ))}
        </div>
      </div>

      <div style={styles.performanceSummary}>
        <h3 style={styles.performanceHeading}>Overall Performance</h3>
        <p style={styles.performanceText}>
          The student has an average of <strong>{averageMarks}%</strong> across all subjects.
        </p>
      </div>
    </div>
  );
}

const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    padding: '30px',
    backgroundColor: '#f4f7fc',
    borderRadius: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    maxWidth: '800px',
    margin: '40px auto',
  },
  title: {
    textAlign: 'center',
    color: '#333',
    marginBottom: '30px',
    fontSize: '30px',
    fontWeight: 'bold',
  },
  subjectsContainer: {
    marginBottom: '30px',
  },
  heading: {
    fontSize: '22px',
    color: '#333',
    marginBottom: '15px',
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
  },
  tableRow: {
    display: 'flex',
    justifyContent: 'space-between',
    padding: '12px 0',
    borderBottom: '1px solid #ddd',
  },
  tableHeader: {
    fontWeight: 'bold',
    fontSize: '16px',
    color: '#007BFF',
    flex: 1,
    textAlign: 'left',
    paddingLeft: '10px',
  },
  tableData: {
    fontSize: '14px',
    color: '#555',
    flex: 1,
    textAlign: 'left',
    paddingLeft: '10px',
  },
  performanceSummary: {
    textAlign: 'center',
    marginTop: '20px',
    padding: '15px',
    backgroundColor: '#fff',
    borderRadius: '8px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
  },
  performanceHeading: {
    fontSize: '20px',
    color: '#333',
    marginBottom: '10px',
  },
  performanceText: {
    fontSize: '16px',
    color: '#555',
    lineHeight: '1.6',
  },
};

export default Performance;
