import React, { useState, useEffect } from 'react';
import './UserHome.css';
import { Link, useNavigate } from 'react-router-dom';
import { FaUser, FaBook, FaShoppingCart, FaHistory, FaUndoAlt, FaSignOutAlt, FaBars } from 'react-icons/fa';

const UserHome = () => {
  const navigate = useNavigate();
  const [isSidebarVisible, setSidebarVisible] = useState(false);
  const [resources, setResources] = useState([]);

  useEffect(() => {
    // Fetch resources from the backend
    fetch("http://localhost:8080/api/resources")
      .then((response) => response.json())
      .then((data) => setResources(data))
      .catch((error) => console.error("Error fetching resources:", error));
  }, []);

  const handleLogout = () => {
    navigate('/login'); // Redirect to the login page
  };

  const toggleSidebar = () => {
    setSidebarVisible(!isSidebarVisible);
  };

  return (
    <div className="home-container">
      {!isSidebarVisible && (
        <button onClick={toggleSidebar} className="menu-toggle">
          <FaBars />
        </button>
      )}

      {isSidebarVisible && (
        <aside className="sidebar">
          <h2 className="sidebar-title" onClick={() => setSidebarVisible(false)}>EduConnect</h2>
          <nav className="sidebar-menu">
            <Link to="/user-profile" className="menu-item">
              <FaUser className="menu-icon" /> User Profile
            </Link>
            {/* Other sidebar items here */}
          </nav>
          <button onClick={handleLogout} className="logout-button">
            <FaSignOutAlt className="menu-icon" /> Logout
          </button>
        </aside>
      )}

      <div className={`content-wrapper ${isSidebarVisible ? 'shifted-content' : ''}`}>
        <header className="header">
          <h1 className="header-title">Welcome to EduConnect</h1>
        </header>

        <div className="main-content">
          <h2>Available Resources</h2>
          <div className="resource-grid">
            {resources.map((resource) => (
              <div key={resource.id} className="resource-card">
                {resource.coverPhotoUrl && (
                  <img src={`http://localhost:8080/${resource.coverPhotoUrl}`} alt="Cover" className="resource-cover" />
                )}
                <h3>{resource.title}</h3>
                <p>{resource.description}</p>
                <button onClick={() => navigate(`/resource/${resource.id}`)}>View Details</button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserHome;
