import React, { useState } from 'react';
import './UploadResource.css';

function UploadResource() {
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        category: '',
        author: '',
        publicationDate: '',
    });
    const [coverPhoto, setCoverPhoto] = useState(null);
    const [pdfFile, setPdfFile] = useState(null);
    const [message, setMessage] = useState("");

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleFileChange = (e) => {
        const { name, files } = e.target;
        if (name === 'coverPhoto') {
            setCoverPhoto(files[0]);
        } else if (name === 'pdfFile') {
            setPdfFile(files[0]);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const data = new FormData();
        data.append('title', formData.title);
        data.append('description', formData.description);
        data.append('category', formData.category);
        data.append('author', formData.author);
        data.append('publicationDate', formData.publicationDate);

        if (coverPhoto) data.append('coverPhoto', coverPhoto);
        if (pdfFile) data.append('pdfFile', pdfFile);

        try {
            const response = await fetch('http://localhost:8080/api/upload', {
                method: 'POST',
                body: data,
            });

            const responseData = await response.json();

            if (response.ok) {
                setMessage(responseData.message || 'Resource uploaded successfully!');
            } else {
                setMessage('Error: Could not upload the resource');
            }
        } catch (error) {
            console.error('Upload error:', error);
            setMessage('Error: An error occurred during upload');
        }
    };

    return (
        <div className="form-container">
            <h2>Upload New Resource</h2>
            <form onSubmit={handleSubmit} className="form">
                {/* Other form fields */}
                <div className="form-group">
                    <label htmlFor="coverPhoto">Cover Photo</label>
                    <label htmlFor="coverPhoto" className="custom-file-upload">
                        <div className="icon">
                            <svg viewBox="0 0 24 24" fill="" xmlns="http://www.w3.org/2000/svg">
                                <path fillRule="evenodd" clipRule="evenodd" d="M10 1C9.73478 1 9.48043 1.10536 9.29289 1.29289L3.29289 7.29289C3.10536 7.48043 3 7.73478 3 8V20C3 21.6569 4.34315 23 6 23H7C7.55228 23 8 22.5523 8 22C8 21.4477 7.55228 21 7 21H6C5.44772 21 5 20.5523 5 20V9H10C10.5523 9 11 8.55228 11 8V3H18C18.5523 3 19 3.44772 19 4V9C19 9.55228 19.4477 10 20 10C20.5523 10 21 9.55228 21 9V4C21 2.34315 19.6569 1 18 1H10ZM9 7H6.41421L9 4.41421V7ZM14 15.5C14 14.1193 15.1193 13 16.5 13C17.8807 13 19 14.1193 19 15.5V16V17H20C21.1046 17 22 17.8954 22 19C22 20.1046 21.1046 21 20 21H13C11.8954 21 11 20.1046 11 19C11 17.8954 11.8954 17 13 17H14V16V15.5ZM16.5 11C14.142 11 12.2076 12.8136 12.0156 15.122C10.2825 15.5606 9 17.1305 9 19C9 21.2091 10.7909 23 13 23H20C22.2091 23 24 21.2091 24 19C24 17.1305 22.7175 15.5606 20.9844 15.122C20.7924 12.8136 18.858 11 16.5 11Z" fill="#e8e8e8"></path>
                            </svg>
                        </div>
                        <div className="text">
                            <span>{coverPhoto ? coverPhoto.name : 'Click to upload image'}</span>
                        </div>
                        <input id="coverPhoto" name="coverPhoto" type="file" onChange={handleFileChange} accept="image/*" />
                    </label>
                </div>

                <div className="form-group">
                    <label htmlFor="pdfFile">PDF File</label>
                    <label htmlFor="pdfFile" className="custom-file-upload">
                        <div className="icon">
                            <svg viewBox="0 0 24 24" fill="" xmlns="http://www.w3.org/2000/svg">
                                {/* SVG path for the file upload icon */}
                            </svg>
                        </div>
                        <div className="text">
                            <span>{pdfFile ? pdfFile.name : 'Click to upload PDF'}</span>
                        </div>
                        <input id="pdfFile" name="pdfFile" type="file" onChange={handleFileChange} accept=".pdf" />
                    </label>
                </div>

                <button type="submit" className="form-submit-btn">Upload Resource</button>
            </form>
            {message && <p className="message">{message}</p>}
        </div>
    );
}

export default UploadResource;
