import React from 'react';
import { Link } from 'react-router-dom';
import './Welcome.css';

function Welcome() {
    return (
        <div className="welcome-container">
            {/* Header with Right-Aligned Buttons */}
            <div className="header">
                <Link to="/login" className="welcome-button">Log In</Link>
                <Link to="/signup" className="welcome-button">Sign Up</Link>
                <Link to="/about" className="welcome-button">About Us</Link>
                <Link to="/contact" className="welcome-button">Contact</Link>
            </div>

            {/* Welcome Message */}
            <h1 className="welcome-title">Welcome to EduConnect!</h1>
            <p className="welcome-text">
                EduConnect is your gateway to educational resources. From textbooks to research papers,
                explore, learn, and collaborate with ease. Please log in or sign up to get started.
            </p>

            {/* Functionality Description */}
            <div className="website-functionality">
                <h3>How it Works:</h3>
                <ul>
                    <li>Admins can upload and organize resources like textbooks and study guides.</li>
                    <li>Users can search and access educational materials, download resources, and provide feedback.</li>
                    <li>Browse a wide variety of subjects to enhance your learning and research experience.</li>
                </ul>
            </div>

            {/* Footer with Copyright */}
            <div className="footer">
                <p>&copy; 2024 EduConnect. All Rights Reserved.</p>
            </div>
        </div>
    );
}

export default Welcome;
