import React, { useState } from 'react';
import { FaHome, FaUpload, FaFolder, FaUsers } from 'react-icons/fa';

function AdminHome() {
  const [activeTab, setActiveTab] = useState("Dashboard");

  const renderContent = () => {
    switch (activeTab) {
      case "Upload":
        return (
          <div className="flex flex-col items-center justify-center space-y-6">
            <h2 className="text-3xl font-bold">Upload and organize educational resources</h2>
            <p className="text-lg text-gray-400">
              Easily add new materials to the library and categorize them for better organization.
            </p>
            <button className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-md">
              Upload Resources
            </button>
          </div>
        );
      case "Organize":
        return (
          <div className="flex flex-col items-center justify-center space-y-6">
            <h2 className="text-3xl font-bold">Organize resources into categories</h2>
            <p className="text-lg text-gray-400">
              Create and manage custom categories to keep your library structured and searchable.
            </p>
            <button className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-md">
              Manage Categories
            </button>
          </div>
        );
      case "Manage":
        return (
          <div className="flex flex-col items-center justify-center space-y-6">
            <h2 className="text-3xl font-bold">Manage user access</h2>
            <p className="text-lg text-gray-400">
              Control which users can access and interact with the educational resources.
            </p>
            <button className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-md">
              Manage User Permissions
            </button>
          </div>
        );
      default:
        return (
          <div className="flex flex-col items-center justify-center space-y-6">
            <h2 className="text-3xl font-bold">Welcome to the Admin Dashboard</h2>
            <p className="text-lg text-gray-400">
              Manage the educational resource library from this central hub.
            </p>
            <div className="grid grid-cols-2 gap-6">
              <div className="bg-gray-800 rounded-lg p-6 shadow-lg hover:shadow-xl transition-shadow duration-300">
                <h3 className="text-2xl font-bold text-blue-500 mb-2">Upload Resources</h3>
                <p className="text-gray-400">Add new educational materials to the library.</p>
              </div>
              <div className="bg-gray-800 rounded-lg p-6 shadow-lg hover:shadow-xl transition-shadow duration-300">
                <h3 className="text-2xl font-bold text-blue-500 mb-2">Organize Resources</h3>
                <p className="text-gray-400">Manage categories and structure of the library.</p>
              </div>
              <div className="bg-gray-800 rounded-lg p-6 shadow-lg hover:shadow-xl transition-shadow duration-300">
                <h3 className="text-2xl font-bold text-blue-500 mb-2">Manage Users</h3>
                <p className="text-gray-400">Control user access and permissions.</p>
              </div>
              <div className="bg-gray-800 rounded-lg p-6 shadow-lg hover:shadow-xl transition-shadow duration-300">
                <h3 className="text-2xl font-bold text-blue-500 mb-2">View Analytics</h3>
                <p className="text-gray-400">Monitor usage and engagement with the library.</p>
              </div>
            </div>
          </div>
        );
    }
  };

  return (
    <div className="admin-home bg-gray-900 text-white min-h-screen">
      <div className="sidebar bg-gray-800 p-6 shadow-lg">
        <h1 className="logo text-3xl font-bold text-blue-500 mb-6 cursor-pointer">Admin</h1>
        <ul className="space-y-4">
          <li
            onClick={() => setActiveTab("Dashboard")}
            className={`flex items-center space-x-2 cursor-pointer transition-colors duration-300 ${
              activeTab === "Dashboard" ? "text-blue-500" : "text-gray-400 hover:text-blue-500"
            }`}
          >
            <FaHome className="w-5 h-5" />
            <span>Dashboard</span>
          </li>
          <li
            onClick={() => setActiveTab("Upload")}
            className={`flex items-center space-x-2 cursor-pointer transition-colors duration-300 ${
              activeTab === "Upload" ? "text-blue-500" : "text-gray-400 hover:text-blue-500"
            }`}
          >
            <FaUpload className="w-5 h-5" />
            <span>Upload Resources</span>
          </li>
          <li
            onClick={() => setActiveTab("Organize")}
            className={`flex items-center space-x-2 cursor-pointer transition-colors duration-300 ${
              activeTab === "Organize" ? "text-blue-500" : "text-gray-400 hover:text-blue-500"
            }`}
          >
            <FaFolder className="w-5 h-5" />
            <span>Organize Resources</span>
          </li>
          <li
            onClick={() => setActiveTab("Manage")}
            className={`flex items-center space-x-2 cursor-pointer transition-colors duration-300 ${
              activeTab === "Manage" ? "text-blue-500" : "text-gray-400 hover:text-blue-500"
            }`}
          >
            <FaUsers className="w-5 h-5" />
            <span>Manage User Access</span>
          </li>
        </ul>
      </div>
      <div className="main-content p-8 flex-1 overflow-y-auto">
        {renderContent()}
      </div>
    </div>
  );
}

export default AdminHome;
