import React, { useState } from 'react';
import './UserHome.css';
import { Link, useNavigate } from 'react-router-dom';
import { FaUser, FaBook, FaShoppingCart, FaHistory, FaUndoAlt, FaSignOutAlt, FaBars } from 'react-icons/fa';

const UserHome = () => {
  const navigate = useNavigate();
  const [isSidebarVisible, setSidebarVisible] = useState(false);

  const handleLogout = () => {
    navigate('/login'); // Redirect to the login page
  };

  const toggleSidebar = () => {
    setSidebarVisible(!isSidebarVisible);
  };

  const closeSidebar = () => {
    setSidebarVisible(false);
  };

  return (
    <div className="home-container">
      {/* Show menu icon only when sidebar is hidden */}
      {!isSidebarVisible && (
        <button onClick={toggleSidebar} className="menu-toggle">
          <FaBars />
        </button>
      )}

      {isSidebarVisible && (
        <aside className="sidebar">
          <h2 className="sidebar-title" onClick={closeSidebar}>EduConnect</h2>
          <nav className="sidebar-menu">
            <Link to="/user-profile" className="menu-item" onClick={closeSidebar}>
              <FaUser className="menu-icon" /> User Profile
            </Link>
            <Link to="/borrow-books" className="menu-item" onClick={closeSidebar}>
              <FaBook className="menu-icon" /> Borrow Books
            </Link>
            <Link to="/buy-books" className="menu-item" onClick={closeSidebar}>
              <FaShoppingCart className="menu-icon" /> Buy Books
            </Link>
            <Link to="/history" className="menu-item" onClick={closeSidebar}>
              <FaHistory className="menu-icon" /> History
            </Link>
            <Link to="/return-books" className="menu-item" onClick={closeSidebar}>
              <FaUndoAlt className="menu-icon" /> Return Books
            </Link>
          </nav>
          <button onClick={handleLogout} className="logout-button">
            <FaSignOutAlt className="menu-icon" /> Logout
          </button>
        </aside>
      )}

      <div className={`content-wrapper ${isSidebarVisible ? 'shifted-content' : ''}`}>
        <header className="header">
          <h1 className="header-title">Welcome to EduConnect</h1>
          <p className="header-subtitle">Explore a vast library of resources for learning and research.</p>
        </header>

        <div className="main-content">
          <h2 className="section-title">Available Resources</h2>
          <div className="modules">
            <Link to="/textbooks" className="module-card">
              <h3>Textbooks</h3>
              <p>Access a variety of textbooks for your studies.</p>
            </Link>
            <Link to="/research-papers" className="module-card">
              <h3>Research Papers</h3>
              <p>Dive into scholarly articles and research papers.</p>
            </Link>
            <Link to="/study-guides" className="module-card">
              <h3>Study Guides</h3>
              <p>Find comprehensive guides for better understanding.</p>
            </Link>
            <Link to="/lecture-notes" className="module-card">
              <h3>Lecture Notes</h3>
              <p>Explore notes from various educational lectures.</p>
            </Link>
            
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserHome;
