import React, { useState } from 'react';
import './UploadResource.css';

function UploadResource() {
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        category: 'All', // Default category option
        tags: '',
        author: '',
        publicationDate: '',
    });
    const [coverPhoto, setCoverPhoto] = useState(null);
    const [pdfFile, setPdfFile] = useState(null);
    const [message, setMessage] = useState("");

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleFileChange = (e) => {
        const { name, files } = e.target;
        if (name === 'coverPhoto') {
            setCoverPhoto(files[0]);
        } else if (name === 'pdfFile') {
            setPdfFile(files[0]);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const data = new FormData();
        data.append('title', formData.title);
        data.append('description', formData.description);
        data.append('category', formData.category);
        data.append('tags', formData.tags);
        data.append('author', formData.author);
        data.append('publicationDate', formData.publicationDate);
        
        if (coverPhoto) data.append('coverPhoto', coverPhoto);
        if (pdfFile) data.append('pdfFile', pdfFile);

        try {
            const response = await fetch('http://localhost:8080/api/upload', {
                method: 'POST',
                body: data,
            });

            const responseData = await response.json();

            if (response.ok) {
                setMessage(responseData.message || 'Resource uploaded successfully!');
            } else {
                setMessage('Error: Could not upload the resource');
            }
        } catch (error) {
            console.error('Upload error:', error);
            setMessage('Error: An error occurred during upload');
        }
    };

    return (
        <div className="form-container">
            <h2>Upload New Resource</h2>
            <form onSubmit={handleSubmit} className="form">
                <div className="form-group">
                    <label htmlFor="title">Title</label>
                    <input type="text" id="title" name="title" placeholder="Title" onChange={handleChange} required />
                </div>
                <div className="form-group">
                    <label htmlFor="description">Description</label>
                    <textarea id="description" name="description" placeholder="Description" onChange={handleChange} />
                </div>
                <div className="form-group">
                    <label>Category</label>
                    <div className="select">
                        <div
                            className="selected"
                            data-default="All"
                            data-books="Books"
                            data-textbooks="Textbooks"
                            data-researchpapers="Research Papers"
                            data-studyguides="Study Guides"
                            data-reference="Reference Materials"
                            data-interactive="Interactive Learning Modules"
                            data-casestudies="Case Studies"
                        >
                            <span>{formData.category}</span>
                            <svg xmlns="http://www.w3.org/2000/svg" height="1em" viewBox="0 0 512 512" className="arrow">
                                <path d="M233.4 406.6c12.5 12.5 32.8 12.5 45.3 0l192-192c12.5-12.5 12.5-32.8 0-45.3s-32.8-12.5-45.3 0L256 338.7 86.6 169.4c-12.5-12.5-32.8-12.5-45.3 0s-12.5 32.8 0 45.3l192 192z"></path>
                            </svg>
                        </div>
                        <div className="options">
                            {['All', 'Books', 'Textbooks', 'Research Papers', 'Study Guides', 'Reference Materials', 'Interactive Learning Modules', 'Case Studies'].map((option, index) => (
                                <div key={index} title={option.toLowerCase().replace(' ', '')}>
                                    <input 
                                        type="radio" 
                                        id={option.toLowerCase()} 
                                        name="category" 
                                        value={option} 
                                        onChange={(e) => setFormData({...formData, category: e.target.value})} 
                                    />
                                    <label className="option" htmlFor={option.toLowerCase()} data-txt={option}></label>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
                {/* Other form fields */}
                <button type="submit" className="form-submit-btn">Upload Resource</button>
            </form>
            {message && <p className="message">{message}</p>}
        </div>
    );
}

export default UploadResource;
