import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./ResourceDetail.css";

const ResourceDetail = () => {
  const { id } = useParams(); // Get resource ID from URL
  const navigate = useNavigate();
  const [resource, setResource] = useState(null); // Resource data
  const [isPdfOpen, setPdfOpen] = useState(false); // PDF Viewer toggle
  const [isBorrowPopupOpen, setBorrowPopupOpen] = useState(false); // Borrow popup toggle
  const [borrowDays, setBorrowDays] = useState(1); // Borrowing days (default: 1)
  const [borrowSuccess, setBorrowSuccess] = useState(false); // Borrow success message toggle

  // Fetch resource details
  useEffect(() => {
    fetch(`http://localhost:8080/api/resources/${id}`)
      .then((response) => response.json())
      .then((data) => setResource(data))
      .catch((error) =>
        console.error("Error fetching resource details:", error)
      );
  }, [id]);

  // Handle borrowing submission
  const handleBorrowSubmit = () => {
    // Simulate API call to borrow the book
    fetch(`http://localhost:8080/api/resources/${id}/borrow`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ days: borrowDays }), // Send the number of days
    })
      .then((response) => {
        if (response.ok) {
          setBorrowSuccess(true); // Show success message
          setBorrowPopupOpen(false); // Close the popup
        } else {
          throw new Error("Failed to borrow the book");
        }
      })
      .catch((error) => console.error("Error borrowing book:", error));
  };

  // If resource data is not yet loaded, show loading state
  if (!resource) {
    return <p>Loading...</p>;
  }

  return (
    <div className="resource-detail-container">
      <button onClick={() => navigate(-1)} className="back-button">
        Back
      </button>
      <h2 className="resource-title">{resource.title}</h2>
      {resource.coverPhotoUrl && (
        <img
          src={`http://localhost:8080${resource.coverPhotoUrl}`}
          alt={resource.title}
          className="detail-cover"
        />
      )}
      <p>
        <strong>Author:</strong> {resource.author}
      </p>
      <p>
        <strong>Category:</strong> {resource.category}
      </p>
      <p>
        <strong>Publication Date:</strong> {resource.publicationDate}
      </p>
      <p>
        <strong>Description:</strong> {resource.description}
      </p>

      <div className="action-buttons">
        <button onClick={() => setPdfOpen(true)} className="read-button">
          Read
        </button>
        <button className="buy-button">Buy</button>
        <button
          onClick={() => setBorrowPopupOpen(true)} // Open borrow popup
          className="borrow-button"
        >
          Borrow
        </button>
        <button className="return-button">Return</button>
      </div>

      {/* PDF Viewer */}
      {isPdfOpen && resource.pdfFileUrl && (
        <div className="pdf-viewer">
          <button
            onClick={() => setPdfOpen(false)}
            className="close-pdf-button"
          >
            X
          </button>
          <iframe
            src={`http://localhost:8080${resource.pdfFileUrl}`}
            title="PDF Viewer"
            className="pdf-frame"
          ></iframe>
        </div>
      )}

      {/* Borrow Popup */}
      {isBorrowPopupOpen && (
        <div className="borrow-popup">
          <div className="popup-content">
            <h3>Borrow Book</h3>
            <p>You can borrow this book for up to 40 days.</p>
            <label htmlFor="borrow-days">Select Days:</label>
            <input
              type="number"
              id="borrow-days"
              min="1"
              max="40"
              value={borrowDays}
              onChange={(e) => setBorrowDays(e.target.value)}
            />
            <div className="popup-buttons">
              <button onClick={handleBorrowSubmit} className="borrow-submit">
                Borrow
              </button>
              <button
                onClick={() => setBorrowPopupOpen(false)} // Close popup
                className="popup-cancel"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Borrow Success Message */}
      {borrowSuccess && (
        <p className="success-message">The book has been successfully borrowed!</p>
      )}
    </div>
  );
};

export default ResourceDetail;
