import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { FaUser, FaBook, FaBriefcase, FaHistory, FaSignOutAlt, FaBars } from "react-icons/fa";
import "./UserHome.css";

const UserHome = () => {
  const navigate = useNavigate();
  const [isSidebarVisible, setSidebarVisible] = useState(false);
  const [isHovered, setIsHovered] = useState({});
  const [resources, setResources] = useState([]);
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    category: "",
    tags: "",
    publicationDate: "",
    file: null,
    coverPhoto: null,
  });

  // Fetch resources from the backend when the component mounts
  useEffect(() => {
    fetch("http://localhost:8080/api/resources")
      .then((response) => response.json())
      .then((data) => setResources(data))
      .catch((error) => console.error("Error fetching resources:", error));
  }, []);

  // Handle logout functionality
  const handleLogout = () => {
    navigate("/login"); // Redirect to the login page
  };

  // Toggle sidebar visibility
  const toggleSidebar = () => {
    setSidebarVisible(!isSidebarVisible);
  };

  // Handle file change
  const handleFileChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.files[0] });
  };

  // Handle form data change
  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle resource upload
  const handleResourceUpload = (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append("title", formData.title);
    data.append("description", formData.description);
    data.append("category", formData.category);
    data.append("tags", formData.tags);
    data.append("publicationDate", formData.publicationDate);
    if (formData.file) data.append("file", formData.file);
    if (formData.coverPhoto) data.append("coverPhoto", formData.coverPhoto);

    fetch("http://localhost:8080/api/upload", {
      method: "POST",
      body: data,
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data.message);
        // Reload the resources
        fetch("http://localhost:8080/api/resources")
          .then((response) => response.json())
          .then((data) => setResources(data));
      })
      .catch((error) => console.error("Error uploading resource:", error));
  };

  // Manage mouse enter/leave events for hover effects on modules
  const handleMouseEnter = (module) => {
    setIsHovered((prev) => ({ ...prev, [module]: true }));
  };

  const handleMouseLeave = (module) => {
    setIsHovered((prev) => ({ ...prev, [module]: false }));
  };

  return (
    <div className="home-container">
      <button onClick={toggleSidebar} className="menu-toggle">
        <FaBars />
      </button>
      {isSidebarVisible && (
        <aside className="sidebar">
          <h2 className="sidebar-title" onClick={() => setSidebarVisible(false)}>
            PlacementConnect
          </h2>
          <nav className="sidebar-menu">
            <a className="menu-item" onClick={() => navigate("/user-profile")}>
              <FaUser className="menu-icon" /> User Profile
            </a>
            <a className="menu-item" onClick={() => navigate("/placement-opportunities")}>
              <FaBriefcase className="menu-icon" /> Placement Opportunities
            </a>
            <a className="menu-item" onClick={() => navigate("/placement-history")}>
              <FaHistory className="menu-icon" /> Placement History
            </a>
          </nav>
          <button onClick={handleLogout} className="logout-button">
            <FaSignOutAlt className="menu-icon" /> Logout
          </button>
        </aside>
      )}

      <div className={`content-wrapper ${isSidebarVisible ? "shifted-content" : ""}`}>
        <header className="header">
          <h1 className="header-title">Welcome to Placement Portal</h1>
        </header>

        <form onSubmit={handleResourceUpload} className="upload-form">
          <h2>Upload Resource</h2>
          <input type="text" name="title" value={formData.title} onChange={handleInputChange} placeholder="Title" />
          <textarea name="description" value={formData.description} onChange={handleInputChange} placeholder="Description" />
          <input type="text" name="category" value={formData.category} onChange={handleInputChange} placeholder="Category" />
          <input type="text" name="tags" value={formData.tags} onChange={handleInputChange} placeholder="Tags" />
          <input type="date" name="publicationDate" value={formData.publicationDate} onChange={handleInputChange} />
          <input type="file" name="file" onChange={handleFileChange} />
          <input type="file" name="coverPhoto" onChange={handleFileChange} />
          <button type="submit">Upload Resource</button>
        </form>

        <div className="resources-list">
          <h2>Uploaded Resources</h2>
          {resources.map((resource) => (
            <div key={resource.id} className="resource-item">
              {resource.coverPhoto && (
                <img src={`http://localhost:8080/${resource.coverPhoto}`} alt="cover" className="cover-photo" />
              )}
              <h3>{resource.title}</h3>
              <p>{resource.description}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default UserHome;
