import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./ResourceDetail.css";

const ResourceDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [resource, setResource] = useState(null);
  const [isPdfOpen, setPdfOpen] = useState(false);

  useEffect(() => {
    fetch(`http://localhost:8080/api/resources/${id}`)
      .then((response) => response.json())
      .then((data) => setResource(data))
      .catch((error) => console.error("Error fetching resource details:", error));
  }, [id]);

  if (!resource) {
    return <p>Loading...</p>;
  }

  const handleRead = () => {
    setPdfOpen(true);
  };

  const handleClosePdf = () => {
    setPdfOpen(false);
  };

  return (
    <div className="resource-detail-container">
      <button onClick={() => navigate("/user-home")} className="back-button">
        Back
      </button>
      <h2>{resource.title}</h2>
      {resource.coverPhotoUrl && (
        <img
          src={`http://localhost:8080${resource.coverPhotoUrl}`}
          alt={resource.title}
          className="detail-cover"
        />
      )}
      <p><strong>Author:</strong> {resource.author}</p>
      <p><strong>Category:</strong> {resource.category}</p>
      <p><strong>Publication Date:</strong> {resource.publicationDate}</p>
      <p><strong>Description:</strong> {resource.description}</p>

      <div className="action-buttons">
        <button onClick={handleRead} className="read-button">
          Read
        </button>
        <button className="buy-button">Buy</button>
        <button className="borrow-button">Borrow</button>
        <button className="return-button">Return</button>
      </div>

      {isPdfOpen && resource.pdfFileUrl && (
        <div className="pdf-viewer">
          <button onClick={handleClosePdf} className="close-pdf-button">X</button>
          <iframe
            src={`http://localhost:8080${resource.pdfFileUrl}`}
            title="PDF Viewer"
            className="pdf-frame"
          ></iframe>
        </div>
      )}
    </div>
  );
};

export default ResourceDetail;
